import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Form, Input, Button, Checkbox, Row, Typography,Col,Image } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import "./login.css"; // Import your custom CSS file
import background from "./background.jpg"
import logo from "./logo.jpg"
const { Text } = Typography;

export default class Login extends Component {
  onFinish = (values) => {
    console.log('Received values:', values);
    // 登录逻辑
  };

  submit = () => {
    this.props.history.replace('/Data');
  };

  render() {
    return (
       <div className="container mt-5" style={{ backgroundImage: `url(${background})`, backgroundSize: '100% 100%'}}>
            {/* <video autoPlay loop muted className="video-background">
                <source src={background} type="video/mp4" />
                Your browser does not support the video tag.
            </video> */}
        <div className="row justify-content-center">
          <div className="col-md-6">
            <LoginForm onFinish={this.onFinish} submit={this.submit} />
          </div>
        </div>
      </div>
    );
  }
}

class LoginForm extends Component {
  render() {
    return (
      <Row className="card" >
        <Row className="card-body">
        {/* <Col span={4}>
            <Image style="small"
              fallback="data:image/png;base64,"
              src={"./chemical.png"}
              style={{ maxWidth: '70%', height: 'auto' }}
            />
          </Col> */}
         <img src={logo} alt="Logo" />
        <Text className="card-title text-center" style={{ color: 'lightblack' }} >登录PathoVision</Text>
          <Form
            name="normal_login"
            className="login-form"
            initialValues={{ remember: true }}
            onFinish={this.props.onFinish}
          >
            <Form.Item
              name="username"
              rules={[{ required: true, message: '请输入用户名!' }]}
            >
              <Input prefix={<UserOutlined />} placeholder="用户名" />
            </Form.Item>
            <Form.Item
              name="password"
              rules={[{ required: true, message: '请输入密码!' }]}
            >
              <Input.Password prefix={<LockOutlined />} placeholder="密码" />
            </Form.Item>
            <Form.Item className="form-row" style={{ display: 'flex', justifyContent: 'space-between' }}>
              <Form.Item name="remember" valuePropName="checked" noStyle >
                <Checkbox style={{ color: 'black' }}>记住我</Checkbox>
              </Form.Item>
            </Form.Item>
            <Form.Item>
              <Button type="primary" htmlType="submit" className="login-form-button"     onClick={this.props.submit}>
                登录
              </Button >
              <a href="" style={{ color: 'black' }}>立即注册</a>
            </Form.Item>
          </Form>
        </Row>
      </Row>
    );
  }
}
